<!DOCTYPE html><html> <head>  <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />   <title>注册页面</title>   <script src="/re/js/jquery-1.9.1.min.js" type="text/javascript"></script>   <script type="text/javascript" src="/moban/js/libs/jquery-1.8.3.min.js"></script>  <style>body{	background: #ebebeb;	font-family: "Helvetica Neue","Hiragino Sans GB","Microsoft YaHei","\9ED1\4F53",Arial,sans-serif;	color: #222;	font-size: 12px;}*{padding: 0px;margin: 0px;}.top_div{	background: #008ead;	width: 100%;	height: 300px;}.ipt{	border: 1px solid #d3d3d3;	padding: 10px 10px;	width: 200px;	border-radius: 4px;	padding-left: 35px;	padding-top:20px;	-webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);	box-shadow: inset 0 1px 1px rgba(0,0,0,.075);	-webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;	-o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;	transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s}.ipt:focus{	border-color: #66afe9;	outline: 0;	-webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6);	box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(102,175,233,.6)}.u_logo{	background: url("/re/images/username.png") no-repeat;	padding: 10px 10px;	position: absolute;	top: 43px;	left: 40px;}.p_logo{	background: url("/re/images/password.png") no-repeat;	padding: 10px 10px;	position: absolute;	top: 12px;	left: 40px;}a{	text-decoration: none;}.tou{	background: url("/re/images/tou.png") no-repeat;	width: 97px;	height: 92px;	position: absolute;	top: -87px;	left: 140px;}.left_hand{	background: url("/re/images/left_hand.png") no-repeat;	width: 32px;	height: 37px;	position: absolute;	top: -38px;	left: 150px;}.right_hand{	background: url("/re/images/right_hand.png") no-repeat;	width: 32px;	height: 37px;	position: absolute;	top: -38px;	right: -64px;}.initial_left_hand{	background: url("/re/images/hand.png") no-repeat;	width: 30px;	height: 20px;	position: absolute;	top: -12px;	left: 100px;}.initial_right_hand{	background: url("/re/images/hand.png") no-repeat;	width: 30px;	height: 20px;	position: absolute;	top: -12px;	right: -112px;}.left_handing{	background: url("/re/images/left-handing.png") no-repeat;	width: 30px;	height: 20px;	position: absolute;	top: -24px;	left: 139px;}.right_handinging{	background: url("/re/images/right_handing.png") no-repeat;	width: 30px;	height: 20px;	position: absolute;	top: -21px;	left: 210px;}#one{	position:relative;	padding-left: 380px;	top: -40px;}</style>   <script type="text/javascript">$(function(){	//得到焦点	$("#password").focus(function(){		$("#left_hand").animate({			left: "150",			top: " -38"		},{step: function(){			if(parseInt($("#left_hand").css("left"))>140){				$("#left_hand").attr("class","left_hand");			}		}}, 2000);		$("#right_hand").animate({			right: "-64",			top: "-38px"		},{step: function(){			if(parseInt($("#right_hand").css("right"))> -70){				$("#right_hand").attr("class","right_hand");			}		}}, 2000);	});	//失去焦点	$("#password").blur(function(){		$("#left_hand").attr("class","initial_left_hand");		$("#left_hand").attr("style","left:100px;top:-12px;");		$("#right_hand").attr("class","initial_right_hand");		$("#right_hand").attr("style","right:-112px;top:-12px");	});});</script>   <meta name="GENERATOR" content="MSHTML 11.00.9600.17496" /> </head>  <body>   <div class="top_div">  	@if (count($errors) > 0)                    <div class="mws-form-message info">                        <div class="alert alert-danger">                            <ul>                                @foreach ($errors->all() as $error)                                    <li>{{ $error }}</li>                                @endforeach                            </ul>                        </div>                    </div>                    @endif  